import React from 'react'
import iconFiltrer from '../assets/filtrer.svg'
import '../styles/FiltreEntrep.css'
export  default function Filtrer(){



  function manageClickFiltrer(){
    const overlay = document.getElementById('F_overlay');
    const ban = document.getElementById('banner');
    if (ban.classList.contains('F_hidden')) {
        overlay.classList.add('F_Svisible');
        overlay.classList.remove('F_Shidden');
        ban.classList.add('F_visible');
        ban.classList.remove('F_Shidden');
      } else {
        overlay.classList.add('F_Shidden');
        overlay.classList.remove('F_Svisible');
        ban.classList.add('F_Svisible');
        ban.classList.remove('F_Shidden');
      }
}
 return(
    <React.Fragment>
    <div className='filtrer' id='F_Banner' onClick={()=>manageClickFiltrer()}>
        <button className="icon-button">
            <img src={iconFiltrer} alt="Microphone"/>
        </button>
        <div  id='banner' className='banner_Filtrer F_hidden'>

        </div>
    </div>
    <div id="F_overlay" className="F_Shidden"></div>

    </React.Fragment>
 )
}
